import 'package:flutter/material.dart';
import 'package:flutter_screenutil/flutter_screenutil.dart';
import 'package:get/get.dart';
import 'package:kallukoshai/view/ui/screens/photo%20_search/photo_search_product_item.dart';
import '../../../../helper/helper.dart';
import '../../../../states/graphQL/controller/home_controller.dart';
import '../../widgets/custom_local_search_box.dart';
import '../../widgets/custom_titlebar.dart';

class PhotoSearchResult extends StatefulWidget {
  static String routeName = '/photoSearchResult';

  const PhotoSearchResult({Key? key}) : super(key: key);

  @override
  State<PhotoSearchResult> createState() => _PhotoSearchResultState();
}

class _PhotoSearchResultState extends State<PhotoSearchResult> {
  final HomeControllerGraphql _homeCon = Get.find<HomeControllerGraphql>();

  @override
  void initState() {
    var image = Get.arguments[0];
    _homeCon.getImageSearchData(image);
    // _homeCon.getImageSearchData("");
    print('img: $image');
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      resizeToAvoidBottomInset: false,
      body: SingleChildScrollView(
        child: Padding(
          padding: const EdgeInsets.symmetric(horizontal: 20.0, vertical: 30),
          child: Column(
            children: [
              addH(10.h),
              const CustomTitleBar(
                title: 'Photo Search',
                editIcon: '',
              ),
              Obx(
                () {
                  if (_homeCon.imageSearchLoadingFlag.value) {
                    return const SizedBox(
                      height: 500,
                      child: Center(
                        child: CircularProgressIndicator(
                            //  color: Colors.yellow,
                            ),
                      ),
                    );
                  } else {
                    if (_homeCon.imageSearchModel != null) {
                      return Column(
                        children: [
                          addH(20.h),
                          SizedBox(
                            height: 220.h,
                            width: double.infinity.w,
                            child: Image.network(
                              _homeCon.imageSearchModel!.productsearchbyimage
                                  .fileUrl,
                              fit: BoxFit.fill,
                              width: double.infinity,
                              height: 220.h,
                            ),
                          ),
                          addH(20.h),
                          CustomLocalSearchBox(
                            searchFn: (value) => _homeCon.filterProduct(value),
                          ),
                          _homeCon.imageSearchModel!.productsearchbyimage.items
                                  .isNotEmpty
                              ? SizedBox(
                                  height: 550.h,
                                  width: double.infinity,
                                  child: ListView.builder(
                                      scrollDirection: Axis.vertical,
                                      physics:
                                          const NeverScrollableScrollPhysics(),
                                      shrinkWrap: true,
                                      itemCount: _homeCon
                                          .photoSearchProductItems.value.length,
                                      itemBuilder: (context, index) {
                                        return PhotoSearchProductItem(
                                          item: _homeCon.photoSearchProductItems
                                              .value[index],
                                        );
                                      }),
                                )
                              : const SizedBox(
                                  height: 500,
                                  child: Center(child: Text('No Item Found!')),
                                ),
                        ],
                      );
                    } else {
                      return const Center(
                        child: Text('No Item Found!'),
                      );
                    }
                  }
                },
              ),
            ],
          ),
        ),
      ),
    );
  }
}
